declare interface IScriptReachable {
    /**
     * Retorna posição X
     * @returns {Number} x
     */
    getX(): number;

    /**
     * Retorna posição Y
     * @returns {Number} y
     */
    getY(): number;

    /**
     * Retorna posição Z
     * @returns {Number} z
    */
    getZ(): number;
}